import React, { useState } from "react";
import './play.css'; // Make sure to replace with the correct CSS file

function Quiz2() {
  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);

  const questions = [
    // ... (same as before)
  ];

  const optionClicked = (isCorrect) => {
    if (selectedOption !== null) {
      if (isCorrect) {
        setScore(score + 1);
      }
      if (currentQuestion + 1 < questions.length) {
        setCurrentQuestion(currentQuestion + 1);
        setSelectedOption(null); // Reset selected option for the next question
      } else {
        setFinalResults(true);
      }
    }
  };

  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
    setSelectedOption(null);
  };

  return (
    <div className="vid-quiz-app">
      <h1>Child Rights</h1>

      {showFinalResults ? (
        <div className="vid-quiz-final-result">
          <h1>Final result</h1>
          <h2>
            {score} out of {questions.length} correct - (
            {(score / questions.length) * 100}%)
          </h2>
          <button
            className="vid-quiz-restart-button"
            onClick={() => restartGame()}
          >
            Restart Game
          </button>
        </div>
      ) : (
        <div className="vid-quiz-questions-card">
          <h2>
            Question {currentQuestion + 1} out of {questions.length}
          </h2>
          <h3 className="vid-quiz-question-text">{questions[currentQuestion].text}</h3>

          <ul>
            {questions[currentQuestion].options.map((option) => (
              <li
                className="vid-quiz-radio-option"
                key={option.id}
              >
                <input
                  type="radio"
                  id={`option-${option.id}`}
                  name="quiz-options"
                  checked={selectedOption === option.id}
                  onChange={() => setSelectedOption(option.id)}
                />
                <label htmlFor={`option-${option.id}`}>{option.text}</label>
              </li>
            ))}
          </ul>

          <button
            className="vid-quiz-submit-button"
            onClick={() => optionClicked(questions[currentQuestion].options[selectedOption]?.isCorrect)}
          >
            Submit
          </button>
        </div>
      )}
    </div>
  );
}

export default Quiz2;
