import React, { useState } from "react";
import "./QuizComponent.css"; // Make sure to replace with the correct CSS file

function QuizComponent() {
  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);

  const questions = [
    // ... (same as before)
  ];

  const optionClicked = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setFinalResults(true);
    }
  };

  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
  };

  return (
    <div className="vid-quiz-app">
      <h1>Child Rights Quiz</h1>

      {showFinalResults ? (
        <div className="vid-quiz-final-result">
          <h1>Final Result</h1>
          <h2>
            {score} out of {questions.length} correct - (
            {(score / questions.length) * 100}%)
          </h2>
          <button
            className="vid-quiz-restart-button"
            onClick={() => restartGame()}
          >
            Restart Quiz
          </button>
        </div>
      ) : (
        <div className="vid-quiz-questions-card">
          <h2>
            Question {currentQuestion + 1} out of {questions.length}
          </h2>
          <h3 className="vid-quiz-question-text">
            {questions[currentQuestion].text}
          </h3>

          <form>
            {questions[currentQuestion].options.map((option) => (
              <div key={option.id} className="vid-quiz-radio-option">
                <input
                  type="radio"
                  id={`vid-quiz-option${option.id}`}
                  name="vid-quiz-option"
                  onClick={() => optionClicked(option.isCorrect)}
                />
                <label htmlFor={`vid-quiz-option${option.id}`}>
                  {option.text}
                </label>
              </div>
            ))}
          </form>
          <button
            className="vid-quiz-submit-button"
            onClick={() => optionClicked(false)}
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}

export default QuizComponent;
