import React, { useState } from "react";
import './play.css'

function Quiz4() {
  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  
  const optionClicked = (isCorrect) => {
    if (selectedOption !== null) {
      if (isCorrect) {
        setScore(score + 1);
      }
      if (currentQuestion + 1 < question.length) {
        setCurrentQuestion(currentQuestion + 1);
        setSelectedOption(null); // Reset selected option for the next question
      } else {
        setFinalResults(true);
      }
    }
  };

  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
    setSelectedOption(null);
  };

  return (
    <div className="vid-quiz-app">
      <h1>Good Habits</h1>

      {showFinalResults ? (
        <div className="vid-quiz-final-result">
          <h1>Final result</h1>
          <h2>
            {score} out of {question.length} correct - (
            {(score / question.length) * 100}%)
          </h2>
          <button
            className="vid-quiz-restart-button"
            onClick={() => restartGame()}
          >
            Restart Game
          </button>
        </div>
      ) : (
        <div className="vid-quiz-questions-card">
          <h2>
            Question {currentQuestion + 1} out of {question.length}
          </h2>
          <h3 className="vid-quiz-question-text">
            {question[currentQuestion].text}
          </h3>

          <form>
            {question[currentQuestion].options.map((option) => (
              <div
                key={option.id}
                className="vid-quiz-radio-option"
                onClick={() => optionClicked(option.isCorrect)}
              >
                <input
                  type="radio"
                  id={`vid-quiz-option-${option.id}`}
                  name={`vid-quiz-option-${currentQuestion}`}
                  checked={selectedOption === option.id}
                  onChange={() => setSelectedOption(option.id)}
                />
                <label htmlFor={`vid-quiz-option-${option.id}`}>
                  {option.text}
                </label>
              </div>
            ))}
          </form>
          <button
            className="vid-quiz-submit-button"
            onClick={() => optionClicked(false)}
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}

export default Quiz4;
