import React, {useState} from "react";
//import './App.css'

function Quiz4() {

  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const question =[
    {
      text: "What are good habits?",
      options: [
        
       { id:0, text:"Brush your teeth twice a day", isCorrect: true },
       { id:0, text:"Eat healty food", isCorrect: false },
       { id:0, text:"Do exercise everyday", isCorrect: false },
       { id:0, text:"all the above", isCorrect: false },
      ],
    },
    {
      text: "What are bad habits?",
      options: [
        
       { id:0, text:"crying", isCorrect: false },
       { id:0, text:"Not bushing every day", isCorrect: false },
       { id:0, text:"eating junck food", isCorrect: true },
       { id:0, text:"all the above", isCorrect: false },
      ],
    },
    {
      text:   "whom we have to help?",
      options: [
        
       { id:0, text:"boys", isCorrect: false },
       { id:0, text:"Girls", isCorrect: false },
       { id:0, text:"elders", isCorrect: true },
       { id:0, text:"friends", isCorrect: false },
      ],
    },
    {
      text: "what are important in life?",
      options: [
        
       { id:0, text:"bad behaviour", isCorrect: false },
       { id:0, text:"talking", isCorrect: false },
       { id:0, text:"a and b", isCorrect: false },
       { id:0, text:"good behaviour", isCorrect: true },
      ],
    },
    {
      text: "who are important in life?",
      options: [
        
       { id:0, text:"friends", isCorrect: false },
       { id:0, text:"animals", isCorrect: false },
       { id:0, text:"children", isCorrect: false },
       { id:0, text:"parents", isCorrect: true },
      ],
    },
  ];
  const optionClicked = (isCorrect) => {
    if( isCorrect) {
      setScore(score + 1);
    }
    if (currentQuestion + 1 < question.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setFinalResults(true);
    }
  }
  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
  }
  return (
    <div className="vid-App">
      <h1>Good Habits</h1>
  


      {showFinalResults ? (
        <div className="vid-final-result">
        <h1>Final result</h1>
        <h2>{score} out of {question.length} correct - ({(score/question.length) * 100}%)</h2>
        <button onClick={() => restartGame()}>Restart Game</button>
      </div>
      ) : (
      <div className='vid-questions-card'>
        <h2>Question {currentQuestion + 1} out of {question.length}</h2>
        <h3 className="vid-question-text">{question[currentQuestion].text}</h3>
        

        <ul>
          {question[currentQuestion].options.map((options) => {
            return (
              <li onClick={() => optionClicked(options.isCorrect)} key={options.id}>{options.text}</li>
            );
          })}
        </ul>

      </div>
      )}
      
    </div>
  );
}

export default Quiz4;
