import React, { useState } from "react";
import './App.css'

function Quiz4() {
  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const question = [
    {
      text: "What are good habits?",
      options: [
        { id: 0, text: "Brush your teeth twice a day", isCorrect: true },
        { id: 1, text: "Eat healthy food", isCorrect: false },
        { id: 2, text: "Do exercise every day", isCorrect: false },
        { id: 3, text: "all the above", isCorrect: false },
      ],
    },
    {
      text: "What are bad habits?",
      options: [
        { id: 0, text: "crying", isCorrect: false },
        { id: 1, text: "Not brushing every day", isCorrect: false },
        { id: 2, text: "eating junk food", isCorrect: true },
        { id: 3, text: "all the above", isCorrect: false },
      ],
    },
    {
      text: "Whom do we have to help?",
      options: [
        { id: 0, text: "boys", isCorrect: false },
        { id: 1, text: "Girls", isCorrect: false },
        { id: 2, text: "elders", isCorrect: true },
        { id: 3, text: "friends", isCorrect: false },
      ],
    },
    {
      text: "What is important in life?",
      options: [
        { id: 0, text: "bad behavior", isCorrect: false },
        { id: 1, text: "talking", isCorrect: false },
        { id: 2, text: "a and b", isCorrect: false },
        { id: 3, text: "good behavior", isCorrect: true },
      ],
    },
    {
      text: "Who are important in life?",
      options: [
        { id: 0, text: "friends", isCorrect: false },
        { id: 1, text: "animals", isCorrect: false },
        { id: 2, text: "children", isCorrect: false },
        { id: 3, text: "parents", isCorrect: true },
      ],
    },
  ];

  const optionClicked = (isCorrect) => {
    if (selectedOption !== null) {
      if (isCorrect) {
        setScore(score + 1);
      }
      if (currentQuestion + 1 < question.length) {
        setCurrentQuestion(currentQuestion + 1);
        setSelectedOption(null); // Reset selected option for the next question
      } else {
        setFinalResults(true);
      }
    }
  };

  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
    setSelectedOption(null);
  };

  return (
    <div className="vid-App">
      <h1>Good Habits</h1>

      {showFinalResults ? (
        <div className="vid-final-result">
          <h1>Final result</h1>
          <h2>
            {score} out of {question.length} correct - (
            {(score / question.length) * 100}%)
          </h2>
          <button onClick={() => restartGame()}>Restart Game</button>
        </div>
      ) : (
        <div className="vid-questions-card">
          <h2>
            Question {currentQuestion + 1} out of {question.length}
          </h2>
          <h3 className="vid-question-text">
            {question[currentQuestion].text}
          </h3>

          <form>
            {question[currentQuestion].options.map((option) => (
              <div
                key={option.id}
                className="vid-quiz-radio-option"
                onClick={() => optionClicked(option.isCorrect)}
              >
                <input
                  type="radio"
                  id={`vid-quiz-option-${option.id}`}
                  name={`vid-quiz-option-${currentQuestion}`}
                  checked={selectedOption === option.id}
                  onChange={() => setSelectedOption(option.id)}
                />
                <label htmlFor={`vid-quiz-option-${option.id}`}>
                  {option.text}
                </label>
              </div>
            ))}
          </form>
          <button
            className="vid-quiz-submit-button"
            onClick={() => optionClicked(false)}
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}

export default Quiz4;
