import React, {useState} from "react";
//import './App.css'

function Quiz1() {

  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const question =[
    {
      text: "Which word is correct to express gratitude?",
      options: [
        
       { id:0, text:"Sorry", isCorrect: false },
       { id:0, text:"Thank you", isCorrect: true },
       { id:0, text:"please", isCorrect: false },
       { id:0, text:"may be", isCorrect: false },
      ],
    },
    {
      text: "What should say to make a request?",
      options: [
        
       { id:0, text:"may be", isCorrect: false },
       { id:0, text:"i want", isCorrect: false },
       { id:0, text:"please", isCorrect: true },
       { id:0, text:"nothing", isCorrect: false },
      ],
    },
    {
      text: "if you hurt someone what we should say?",
      options: [
        
       { id:0, text:"sorry", isCorrect: true },
       { id:0, text:"bye", isCorrect: false },
       { id:0, text:"crying", isCorrect: false },
       { id:0, text:"fighting", isCorrect: false },
      ],
    },
    {
      text: "to get someones attension?",
      options: [
        
       { id:0, text:"No", isCorrect: false },
       { id:0, text:"Hello", isCorrect: false },
       { id:0, text:"Excuse me", isCorrect: true },
       { id:0, text:"please", isCorrect: false },
      ],
    },
    {
      text: "what should we never do?",
      options: [
        
       { id:0, text:"eating ", isCorrect: false },
       { id:0, text:"jumping", isCorrect: false },
       { id:0, text:"helping", isCorrect: false },
       { id:0, text:"hurting animals", isCorrect: true },
      ],
    },
  ];
  const optionClicked = (isCorrect) => {
    if( isCorrect) {
      setScore(score + 1);
    }
    if (currentQuestion + 1 < question.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setFinalResults(true);
    }
  }
  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
  }
  return (
    <div className="vid-App">
      <h1>Good Manners</h1>
  


      {showFinalResults ? (
        <div className="vid-final-result">
        <h1>Final result</h1>
        <h2>{score} out of {question.length} correct - ({(score/question.length) * 100}%)</h2>
        <button  onClick={() => restartGame()} className="vid-start-button">Restart Game</button>
      </div>
      ) : (
      <div className='vid-questions-card'>
        <h2>Question {currentQuestion + 1} out of {question.length}</h2>
        <h3 className="vid-question-text">{question[currentQuestion].text}</h3>
        

        <ul>
          {question[currentQuestion].options.map((options) => {
            return (
              <li onClick={() => optionClicked(options.isCorrect)} key={options.id}>{options.text}</li>
            );
          })}
        </ul>

      </div>
      )}
      
    </div>
  );
}

export default Quiz1;
