import React, {useState} from "react";
import './App.css'

function Quiz2() {

  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const question =[
    {
      text: "How many child rights are there?",
      options: [
        
       { id:0, text:"12", isCorrect: true },
       { id:0, text:"10", isCorrect: false },
       { id:0, text:"8", isCorrect: false },
       { id:0, text:"16", isCorrect: false },
      ],
    },
    {
      text: "if any bad touch ur feeling what should you do?",
      options: [
        
       { id:0, text:"crying", isCorrect: false },
       { id:0, text:"raise the issue", isCorrect: false },
       { id:0, text:"tell to mother", isCorrect: true },
       { id:0, text:"silent", isCorrect: false },
      ],
    },
    {
      text: "what are good habits?",
      options: [
        
       { id:0, text:"helping", isCorrect: true },
       { id:0, text:"over sleep", isCorrect: false },
       { id:0, text:"crying", isCorrect: false },
       { id:0, text:"fighting", isCorrect: false },
      ],
    },
    {
      text: "helpline number for child rights?",
      options: [
        
       { id:0, text:"7698 938 893", isCorrect: false },
       { id:0, text:"123 793 0933", isCorrect: false },
       { id:0, text:"31 20 528 9050.", isCorrect: true },
       { id:0, text:"798 709 9031", isCorrect: false },
      ],
    },
    {
      text: "what are important in life?",
      options: [
        
       { id:0, text:"studing", isCorrect: false },
       { id:0, text:"sleeping", isCorrect: false },
       { id:0, text:"eating well", isCorrect: false },
       { id:0, text:"a and b", isCorrect: true },
      ],
    },
  ];
  const optionClicked = (isCorrect) => {
    if( isCorrect) {
      setScore(score + 1);
    }
    if (currentQuestion + 1 < question.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setFinalResults(true);
    }
  }
  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
  }
  return (
    <div className="vid-App">
      <h1>Child Rights</h1>
  


      {showFinalResults ? (
        <div className="vid-final-result">
        <h1>Final result</h1>
        <h2>{score} out of {question.length} correct - ({(score/question.length) * 100}%)</h2>
        <button onClick={() => restartGame()}>Restart Game</button>
      </div>
      ) : (
      <div className='vid-questions-card'>
        <h2>Question {currentQuestion + 1} out of {question.length}</h2>
        <h3 className="vid-question-text">{question[currentQuestion].text}</h3>
        

        <ul>
          {question[currentQuestion].options.map((options) => {
            return (
              <li onClick={() => optionClicked(options.isCorrect)} key={options.id}>{options.text}</li>
            );
          })}
        </ul>

      </div>
      )}
      
    </div>
  );
}

export default Quiz2;
