import React, { useState } from "react";
import './play.css'; // Make sure to replace with the correct CSS file

function Quiz2() {
  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);

  const questions = [
    // ... (same as before)
  ];

  const optionClicked = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setFinalResults(true);
    }
  };

  const restartGame = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
  };

  return (
    <div className="vid-App">
      <h1>Child Rights</h1>

      {showFinalResults ? (
        <div className="vid-final-result">
          <h1>Final result</h1>
          <h2>
            {score} out of {questions.length} correct - (
            {(score / questions.length) * 100}%)
          </h2>
          <button
            className="vid-quiz-restart-button" // Make sure this class name matches the previous quiz component
            onClick={() => restartGame()}
          >
            Restart Game
          </button>
        </div>
      ) : (
        <div className="vid-questions-card">
          <h2>
            Question {currentQuestion + 1} out of {questions.length}
          </h2>
          <h3 className="vid-question-text">{questions[currentQuestion].text}</h3>

          <ul>
            {questions[currentQuestion].options.map((option) => (
              <li
                className="vid-quiz-radio-option" // Make sure this class name matches the previous quiz component
                onClick={() => optionClicked(option.isCorrect)}
                key={option.id}
              >
                {option.text}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Quiz2;
