import React, { useState } from "react";
import './play.css'

function Quiz4() {
  const [showFinalResults, setFinalResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const questions = [
    {
      text: "What does the term child abuse encompass?",
      options: [
        { id: 0, text: "Any intentional harm or mistreatment of a child", isCorrect: true },
        { id: 1, text: "Providing adequate care and attention to children", isCorrect: false },
        { id: 2, text: "Encouraging children to explore their creativity", isCorrect: false },
        { id: 3, text: "Strict discipline to shape a child's behavior", isCorrect: false },
      ],
    },
    {
      text: "Which of the following is a form of child abuse?",
      options: [
        { id: 0, text: "Supporting a child's education", isCorrect: false },
        { id: 1, text: "Not brushing every day", isCorrect: false },
        { id: 2, text: "eating junk food", isCorrect: true },
        { id: 3, text: "all the above", isCorrect: false },
      ],
    },
    {
      text: "Whom do we have to help?",
      options: [
        { id: 0, text: "boys", isCorrect: false },
        { id: 1, text: "Girls", isCorrect: false },
        { id: 2, text: "elders", isCorrect: true },
        { id: 3, text: "friends", isCorrect: false },
      ],
    },
    {
      text: "What is important in life?",
      options: [
        { id: 0, text: "bad behavior", isCorrect: false },
        { id: 1, text: "talking", isCorrect: false },
        { id: 2, text: "a and b", isCorrect: false },
        { id: 3, text: "good behavior", isCorrect: true },
      ],
    },
    {
      text: "Who are important in life?",
      options: [
        { id: 0, text: "friends", isCorrect: false },
        { id: 1, text: "animals", isCorrect: false },
        { id: 2, text: "children", isCorrect: false },
        { id: 3, text: "parents", isCorrect: true },
      ],
    },
  ];
  const optionClicked = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    setCurrentQuestion(currentQuestion + 1);
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setFinalResults(true);
    }
  };

  const resetButton = () => {
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(false);
  };
  

  return (
    <div className="vid-quiz-app">
      
       {/* Current score */}
      <h1 className="ag2-hscore">Current Score:{score}</h1>

      {showFinalResults ? (
        <div className="vid-quiz-final-result">
          <h1>Final Result</h1>
          <h2>
            {score} out of {questions.length} correct - (
            {(score / questions.length) * 100}%)
          </h2>
          
        </div>
      ) : (
        <div className="vid-quiz-questions-card">
          <h2>
            Question {currentQuestion + 1} out of {questions.length}
          </h2>
          <h3 className="vid-quiz-question-text">
            {questions[currentQuestion].text}
          </h3>
          <ul className="ag1-ul-quiz">
            {questions[currentQuestion].options.map((option) => {
              return (
                <li className="ag1-li-quiz"
                  onClick={() => optionClicked(option.isCorrect)}
                  key={option.id}
                >
                  {option.text}
                </li>
              );
            })}
          </ul>
         
        </div>
      )}
    </div>
  );
}

export default Quiz4;
